import React, { Component } from 'react'
import ReactSearchBox from 'react-search-box'
import axios from 'axios'

const data = [
         {key: "10", value: "b"},
          {key: "11", value: "b"},
          {key: "12", value: "BOC"},
           {key: "13", value: "BOC"}
]

export default class App extends Component {
  constructor(props){
    super(props)
    this.state ={
      cartAmount:800,
      data:[]
    }
  }
  getData= async ()=>{
    const result = await axios.get('http://localhost:5000')
    let formatData = result.data.map((row,index)=>{
    return {
      key:`${index+1}`,
      value:row.name
    }
  })
  this.setState({data:formatData})
  console.log("mounted", formatData);
    }

  render() {
    return (
      <div style={{padding:'50px'}}>
        <div style={{textAlign:'center', width:'50%', margin:'0 auto', flexDirection:'row', display:'flex', justifyContent:'space-evenly'}}>

        <button className="minus" onClick={(e)=>{
          e.preventDefault()
          if(!this.state.cartAmount <=0)
          this.setState({
            cartAmount:this.state.cartAmount-1
          })
        }}>--</button>
        {this.state.cartAmount}
        <button className="plus" onClick={(e)=>{
          e.preventDefault()
          this.setState({
            cartAmount:this.state.cartAmount+1
          })
        }}> +</button>
        </div>
        <ReactSearchBox
          placeholder="Search for coupon"
          data={this.state.data}
          onSelect={async (record) => {
            console.log(record.value, this.state.cartAmount)
            const discount = await axios.post('http://localhost:5000/c',{cartAmount:800, couponCode:record.value})
            console.log("discount", discount.data);
          }}
          onFocus={ async () => {
            console.log('This function is called when is focussed')
         await this.getData()
          }}
          onChange={value => console.log(value)}
          fuseConfigs={{
            threshold: 0.05,
          }}
          value=""
        />
     </div>
    )
  }
}
