  import React, { Component } from 'react'
  import ReactSearchBox from 'react-search-box'
  import axios from 'axios'

  const data = [
           {key: "10", value: "b"},
            {key: "11", value: "b"},
            {key: "12", value: "BOC"},
             {key: "13", value: "BOC"}
  ]

  export default class App extends Component {
    constructor(props){
      super(props)
      this.state ={
        cartAmount:0,
        data:data
      }
    }
    getData= async ()=>{
      const result = await axios.get('http://localhost:5000')
      return result.data
      }
    async componentDidMount(){
      const data = await this.getData()
      const formatData = data.map((row,index)=>{
return {
  key:`${index+1}`,
  value:row.name
}
this.setState({data:formatData})
      })
      console.log("mounted", formatData);
    }
    render() {
      return (
        <div style={{padding:'50px'}}>
          <div style={{textAlign:'center', width:'50%', margin:'0 auto', flexDirection:'row', display:'flex', justifyContent:'space-evenly'}}>

          <button className="minus" onClick={(e)=>{
            e.preventDefault()
            if(!this.state.cartAmount <=0)
            this.setState({
              cartAmount:this.state.cartAmount-1
            })
          }}>--</button>
          {this.state.cartAmount}
          <button className="plus" onClick={(e)=>{
            e.preventDefault()
            this.setState({
              cartAmount:this.state.cartAmount+1
            })
          }}> +</button>
          </div>
          <ReactSearchBox
            placeholder="Search for coupon"
            data={this.state.data}
            onSelect={record => console.log(record)}
            onFocus={() => {
              console.log('This function is called when is focussed')
            }}
            onChange={value => console.log(value)}
            fuseConfigs={{
              threshold: 0.05,
            }}
            value=""
          />
       </div>
      )
    }
  }
