  import React, { Component } from 'react'
  import ReactSearchBox from 'react-search-box'
  const data = [
    {
      key: 'john',
      value: 'John Doe',
    },
    {
      key: 'jane',
      value: 'Jane Doe',
    },
    {
      key: 'mary',
      value: 'Mary Phillips',
    },
    {
      key: 'robert',
      value: 'Robert',
    },
    {
      key: 'karius',
      value: 'Karius',
    },
  ]

  export default class App extends Component {
    constructor(props){
      super(props)
this.state ={
  cartAmount:0
}

    }
    render() {
      return (
        <div>
          <ReactSearchBox
            placeholder="Search for John, Jane or Mary"
            data={data}
            onSelect={record => console.log(record)}
            onFocus={() => {
              console.log('This function is called when is focussed')
            }}
            onChange={value => console.log(value)}
            fuseConfigs={{
              threshold: 0.05,
            }}
            value=""
          />
          <div style={{textAlign:'center', width:'50%', margin:'0 auto', flexDirection:'row', display:'flex', justifyContent}}>

          <div className="minus" onClick={(e)=>{
            if(!this.state.cartAmount <=0)
            this.setState({
              cartAmount:this.state.cartAmount-1
            })
          }}>--</div>
          {this.state.cartAmount}
          <div className="plus" onClick={(e)=>{
            if(!this.state.cartAmount <=0)
            this.setState({
              cartAmount:this.state.cartAmount-1
            })
          }}> +</div>
          </div>

       </div>
      )
    }
  }
