import React, { Component } from 'react'
import ReactSearchBox from 'react-search-box'
import axios from 'axios'

export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      cartAmount: 0,
      data: [],
      valueAfterDiscount: 0,
      couponName: '',
      couponType: '',
      expiryDate: '',
      minPurchase: undefined,
      discount: undefined
    }
  }
  getData = async () => {
    const result = await axios.get('http://localhost:5000')
    let formatData = result.data.map((row, index) => {
      return {
        key: `${index + 1}`,
        value: row.name
      }
    })
    this.setState({ data: formatData })
    console.log("mounted", formatData);
  }

  handleChange = event => {
    console.log(event.target);
    const key = event.target.name
    let state = this.state
    event.preventDefault()
    if(key ==="couponName")
    this.setState({ ...state,couponName: event.target.value });
    if(key ==="couponType")
    this.setState({ ...state,couponType: event.target.value });  
     if(key ==="expiryDate")
    this.setState({ ...state,expiryDate: event.target.value });
    if(key ==="minPurchase")
    this.setState({ ...state,minPurchase: event.target.value });
    if(key ==="discount")
    this.setState({ ...state,discount: event.target.value });
  }
  handleSubmit = event => {
    event.preventDefault();
    const body = {
      couponName: this.state.couponName,
      couponType: this.state.couponType,
      expiryDate: this.state.expiryDate,
      minPurchase: this.state.minPurchase,
      discount: this.state.discount    }
