      .then(res=>{
        console.log(res);
        console.log(res.data);
        window.location = "/retrieve" //This line of code will redirect you once the submission is succeed
      })
  }

  render() {
    return (
      <div style={{ padding: '50px' }}>

        <ReactSearchBox
          placeholder="Search for coupon"
          data={this.state.data}
          onSelect={async (record) => {
            console.log(record.value, this.state.cartAmount)
            const discount = await axios.post('http://localhost:5000/coupon', { cartAmount: this.state.cartAmount, couponCode: record.value })
            console.log("discount", discount.data);
            this.setState({ ...this.state, valueAfterDiscount: discount.data.valueAfterFlat })
          }}
          onFocus={async () => {
            console.log('This function is called when is focussed')
            await this.getData()
          }}
          onChange={value => console.log(value)}
          fuseConfigs={{
            threshold: 0.05,
          }}
          value=""
        />

        <div style={{ textAlign: 'center', width: '50%', margin: '0 auto', flexDirection: 'row', display: 'flex', justifyContent: 'space-evenly', paddingTop: '50px' }}>

          <button className="minus" onClick={(e) => {
            e.preventDefault()
            if (!this.state.cartAmount <= 0)
              this.setState({
                cartAmount: this.state.cartAmount - 50
              })
          }}>-</button>
          <button className="plus" onClick={(e) => {
            e.preventDefault()
            this.setState({
              cartAmount: this.state.cartAmount + 50
            })
          }}> +</button>
valueAfterDiscount = {this.state.valueAfterDiscount ? this.state.valueAfterDiscount : this.state.cartAmount}
cartAmount = {this.state.cartAmount}
        </div>

        <div>
          <h2>HTML Forms</h2>

          <form onSubmit={this.handleSubmit}>
            <label >Coupon Name</label><br />
            <input type="text" id="couponName" name="couponName" value={this.state.couponName}
              onChange={(e) => {
                this.handleChange(e)
              }}
            /><br />
            <label >Type</label><br />
            <input type="text" id="couponType" name="couponType" value={this.state.couponType}    
             onChange={(e) => {
                this.handleChange(e)
              }}/><br />
            <label >Expiry Date</label><br />
            <input type="text" id="expiryDate" name="expiryDate" value={this.state.expiryDate}
                 onChange={(e) => {
                  this.handleChange(e)
                }}/><br />
            <label>Minimum Purchase Amount</label><br />
            <input type="text" id="minPurchase" name="minPurchase" value={this.state.minPurchase}
                 onChange={(e) => {
                  this.handleChange(e)
                }}/><br />
            <label >Discount Amount</label><br />
            <input type="text" id="discount" name="discount" value={this.state.discount}
                 onChange={(e) => {
                  this.handleChange(e)
                }}/><br /><br />
            <button type = "submit"> Create </button>

          </form>
        </div>

      </div>
    )
  }
}
