  import React, { Component } from 'react'
  import ReactSearchBox from 'react-search-box'
  const data = [
    {
      key: 'john',
      value: 'John Doe',
    },
    {
      key: 'jane',
      value: 'Jane Doe',
    },
    {
      key: 'mary',
      value: 'Mary Phillips',
    },
    {
      key: 'robert',
      value: 'Robert',
    },
    {
      key: 'karius',
      value: 'Karius',
    },
  ]

  export default class App extends Component {
    constructor(props){
      super(props)
this.state ={
  cartAmount:0
}

    }
    render() {
      return (
        <div>
          <div style={{textAlign:'center', width:'50%', margin:'0 auto', flexDirection:'row', display:'flex', justifyContent:'space-evenly'}}>

          <button className="minus" onClick={(e)=>{
            e.preventDefault()
            if(!this.state.cartAmount <=0)
            this.setState({
              cartAmount:this.state.cartAmount-1
            })
          }}>--</button>
          {this.state.cartAmount}
          <button className="plus" onClick={(e)=>{
            e.preventDefault()
            this.setState({
              cartAmount:this.state.cartAmount+1
            })
          }}> +</button>
          </div>

       </div>
      )
    }
  }
