import ReactSearchBox from 'react-search-box'
import axios from 'axios'

const data = [
  {
    key: 'a',
    value: 'John Doe',
  },
  {
    key: 'b',
    value: 'Jane Doe',
  },
  {
    key: 'c',
    value: 'Mary Phillips',
  },
  {
    key: 'd',
    value: 'Robert',
  },
  {
    key: 'e',
    value: 'Karius',
  },
]

