import React, {
  Component
} from 'react'
import ReactSearchBox from 'react-search-box'
import axios from 'axios'

export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      cartAmount: 0,
      data: [],
      valueAfterDiscount: 0
    }
  }
  getData = async () => {
    const result = await axios.get('http://localhost:5000')
    let formatData = result.data.map((row, index) => {
      return {
        key: `${index+1}`,
        value: row.name
      }
    })
    this.setState({
      data: formatData
    })
    console.log("mounted", formatData);
  }

  render() {
    return ( <
      div style = {
        {
          padding: '50px'
        }
      } >

      <
      ReactSearchBox placeholder = "Search for coupon"
      data = {
        this.state.data
      }
      onSelect = {
        async (record) => {
          console.log(record.value, this.state.cartAmount)
          const discount = await axios.post('http://localhost:5000/coupon', {
            cartAmount: 800,
            couponCode: record.value
          })
          console.log("discount", discount.data);
          this.setState({
            ...this.state,
            valueAfterDiscount: discount.data.valueAfterFlat
          })
        }
      }
      onFocus = {
        async () => {
          console.log('This function is called when is focussed')
          await this.getData()
        }
      }
      onChange = {
        value => console.log(value)
      }
      fuseConfigs = {
        {
          threshold: 0.05,
        }
      }
      value = "" /
      >

      <
      div style = {
        {
          textAlign: 'center',
          width: '50%',
          margin: '0 auto',
          flexDirection: 'row',
          display: 'flex',
          justifyContent: 'space-evenly',
          paddingTop: '50px'
        }
      } >

      <
      button className = "minus"
      onClick = {
        (e) => {
          e.preventDefault()
          if (!this.state.cartAmount <= 0)
            this.setState({
              cartAmount: this.state.cartAmount - 50
            })
        }
      } > -- < /button> <
      button className = "plus"
      onClick = {
        (e) => {
          e.preventDefault()
          this.setState({
            cartAmount: this.state.cartAmount + 50
          })
        }
      } > + < /button>
      valueAfterDiscount = {
        this.state.valueAfterDiscount ? this.state.valueAfterDiscount : this.state.cartAmount
      }
      cartAmount = {
        this.state.cartAmount
      } <
      /div>

      <
      /div>
    )
  }
}
